function fetchReason() {
  const input = document.getElementById("numberInput").value;
  const result = document.getElementById("result");

  if (input >= 1 && input <= reasons.length) {
    result.textContent = reasons[input - 1];
  } else {
    result.textContent = "N*gga tryna be funny";
  }
}

// Add event listener for the "Enter" key
document.getElementById("numberInput").addEventListener("keydown", function (event) {
  if (event.key === "Enter") {
    fetchReason();
  }
});

// Add event listener for the "Submit" button
document.getElementById("submitButton").addEventListener("click", fetchReason);

const colorButtons = document.querySelectorAll(".color-button");

colorButtons.forEach((button) => {
  button.addEventListener("click", () => {
    // Remove 'selected' class from all buttons
    colorButtons.forEach((btn) => btn.classList.remove("selected"));

    // Add 'selected' class to the clicked button
    button.classList.add("selected");

    // Change the theme of the body
    document.body.className = button.dataset.scheme;
  });
});